import React, { Component } from 'react';
class Counter extends Component {


    componentDidUpdate(prevProps,prevState){
    console.log("prevProps", prevProps);
    console.log("prevState", prevState);
    }

    componentWillUnmount(){
        console.log("unmount");
    }
   // constructor(){
   //     super();
      //  this.handleIncrement=this.handleIncrement.bind(this)
    //}

      
     
     doHandleIncrement=()=>{
          this.handleIncrement({id:1});
    }

    //  renderTags(){
         // if (this.state.Tags.length===0){
          //    return <p>Please they are no Tags in the list"</p>;
        //  }else{
             // return  <ol> {this.state.Tags.map(tag=><li key={tag}>{tag}</li>)}</ol>;
          //}
  //    }
    render() { 
        //console.log(this.props);
        console.log("counter-rendered");
        return (
           
            <div>
           
            <span className={this.getBadgeClasses()}>{this.doAction()}</span>
            <button style={{fontSize:15}} className="btn btn-secondary btn-sm"
             
             onClick = {()=>this.props.onIncrement(this.props.counter) }>Incrememt</button> 

             <button  onClick={()=>this.props.onDelete(this.props.counter.id)} className="btn btn-danger btn-sm m-2">Delete</button>
            
            </div>);
    }
        doAction() {
    const {value}=this.props.counter;
    return  value ===0 ? "zero": value;
        }


    getBadgeClasses(){
        let   classes="m-2 badge-"
        classes+=(this.props.counter.value===0) ?  "warning" : "primary";
        return classes;
    }

}
 
export default Counter;
