    render() { 
        //console.log(this.props);
    
        return (
            <div>
           
            <span className={this.getBadgeClasses()}>{this.doAction()}</span>
            <button style={{fontSize:15}} className="btn btn-secondary btn-sm"
             
             onClick = {()=>this.props.onIncrement(this.props.counter) }>Incrememt</button> 

             <button   className="btn btn-danger btn-sm m-2">Delete</button>
            
            </div>);
    }
        doAction() {
    const {value}=this.props.counter;
    return  value ===0 ? "zero": value;
        }


    getBadgeClasses(){
        let   classes="m-2 badge-"
        classes+=(this.props.counter.value===0) ?  "warning" : "primary";
        return classes;
    }

}
 
export default Counter;
