import React, { Component } from 'react';
class Counter extends Component {

    state = {
        count:1,
        school:0,
        imageUrl:"https://picsum..photos/200",
        Tags:[]
      };

      renderTags(){
          if (this.state.Tags.length===0){
              return "Please they are no Tags in the list";
          }else{
              return  <ol> {this.state.Tags.map(tag=><li key={tag}>{tag}</li>)}</ol>;
          }
      }
    render() { 
    
        return (
            <div>
            <span className={this.getBadgeClasses}>{this.doAction()}</span>
            <button style={{fontSize:30}} className="btn btn-secondary btn-sm">Increment</button> 
            {this.state.Tags.length===0 && 'please create a new tag <br>'}
              {this.renderTags()}
            
            </div>);
    }
        doAction() {
    const {school}=this.state;
    return  school ===0 ? "zero": school;
        }


    getBadgeClasses(){
        let   classes="m-2 text-"
        classes=classes+(this.state.count===0) ?  "warning" : "primary";
        return classes;
    }

}
 
export default Counter;
