import React, { Component } from 'react';
class Counter extends Component {
    constructor(){
        super();
        this.handleIncrement=this.handleIncrement.bind(this);
    }

    state = {
        count:1,
        school:0,
        imageUrl:"https://picsum..photos/200",
        Tags:[]
      };
      handleIncrement(){
          console.log("button clicked",this);
      }

      renderTags(){
          if (this.state.Tags.length===0){
              return <p>Please they are no Tags in the list"</p>;
          }else{
              return  <ol> {this.state.Tags.map(tag=><li key={tag}>{tag}</li>)}</ol>;
          }
      }
    render() { 
    
        return (
            <div>
            <span className={this.getBadgeClasses()}>{this.doAction()}</span>
            <button style={{fontSize:15}} className="btn btn-warning btn-sm" onClick={this.handleIncrement}>Incrememt</button> 

            {this.state.Tags.length===0 && 'please create a new tag !'}
            {this.renderTags()}
            
            </div>);
    }
        doAction() {
    const {school}=this.state;
    return  school ===0 ? "zero": school;
        }


    getBadgeClasses(){
        let   classes="m-2 text-"
        classes+=(this.state.count===0) ?  "warning" : "danger";
        return classes;
    }

}
 
export default Counter;
